mapper.select:
SELECT t.data_set_sn AS 'dataSetSn', t.voyage_number AS 'voyageNumber', t.ship_name AS 'shipName', t.platform_type AS 'platformType', t.platform_name AS 'platformName', t.station_num AS 'stationNum', t.start_time AS 'startTime', t.finish_time AS 'finishTime', t.dive_num AS 'diveNum', t.longitude_layout AS 'longitudeLayout', t.latitude_layout AS 'latitudeLayout', t.depth_layout AS 'depthLayout', t.longitude_work AS 'longitudeWork', t.latitude_work AS 'latitudeWork', t.depth_work AS 'depthWork', t.dev_model AS 'devModel', t.dev_sn AS 'devSn', t.data_format AS 'dataFormat', t.data_status AS 'dataStatus', t.data_file_name AS 'dataFileName', t.dev_type AS 'devType', t.data_exist AS 'dataExist' FROM t_ctd_data_records t
t.del_flag = '0'
and t.voyage_number = #{name}
and t.dev_type = #{id}
and t.platform_type = #{id}
and t.start_time >= #{name} and t.start_time <= #{extra}
and t.data_status = #{id}

select t.data_exist from t_ctd_data_records t
where t.voyage_number = #{voyageNumber}
and t.data_file_name = #{fileName}
and t.del_flag = '0'

mapper.update:
update t_ctd_data_records
set data_exist = #{exist}
where data_file_name = #{fileName}
